import React from 'react';

export const CourseForm = () => {

    // TODO: add an API call to get courses and then swap these hardcoded courses out:
    const courses = [
        {
            id: 1,
            courseName: "CMPSC 8",
            department: "CMPSC",
            prerequisite: "",
        },
        {
            id: 2,
            courseName: "CMPSC 16",
            department: "CMPSC",
            prerequisite: "CMPSC 8",
        },
        {
            id: 3,
            courseName: "CMPSC 24",
            department: "CMPSC",
            prerequisite: "CMPSC 16",
        },
        {
            id: 4,
            courseName: "CMPSC 32",
            department: "CMPSC",
            prerequisite: "CMPSC 24",
        },
        {
            id: 5,
            courseName: "CMPSC 40",
            department: "CMPSC",
            prerequisite: "CMPSC 16",
        }
    ]

    return (
        <>
            <h1>Header</h1>
            <form action="">
                <h5>Enter the courses you have taken:</h5>
                {
                    courses.map((course, index) => {
                        let { id, courseName, department, prerequisite } = course;

                        return (
                            <div>
                                <input type="checkbox" id={`course-id-${id}`} name={courseName} value={courseName}/>
                                <label for={courseName}>{courseName}</label>
                            </div>
                        )
                    })
                }
                <input type="submit" value="Submit"></input>
            </form>
        </>

    )
}
